mapper.select:
select * from board_pro
where writer like CONCAT('%',#{keyword},'%') or subject like CONCAT('%',#{keyword},'%') or content like CONCAT('%',#{keyword},'%') and kind = #{kind} and realm = #{realm} order by notice desc, bno desc limit 20 offset #{off}
where ${search_option} like CONCAT('%',#{keyword},'%') and kind = #{kind} and realm = #{realm} order by notice desc, bno desc limit 20 offset #{off}

select count(*) from board_pro
where writer like CONCAT('%',#{keyword},'%') or subject like CONCAT('%',#{keyword},'%') or content like CONCAT('%',#{keyword},'%') and kind = #{kind} and realm = #{realm}
where ${search_option} like CONCAT('%',#{keyword},'%') and kind = #{kind} and realm = #{realm}
